uniform sampler2D DepthTex;
vec4 CCFragOutput_PostProcess(vec4 color) {
  vec4 worldPos = vec4(0.0);
  #if CC_USE_FOG != 4
    float depth = dot(texture2D(DepthTex, v_uv), vec4(1.0, 1.0 / 255.0, 1.0 / 65025.0, 1.0 / 16581375.0));
    vec3 posHS = vec3(v_uv, depth) * 2.0 - vec3(1.0);
    posHS.xy = cc_cameraPos.w == 0.0 ? vec2(posHS.xy.x, -posHS.xy.y) : posHS.xy;
    worldPos = GetWorldPosFromNDCPosRH(posHS, cc_matProj, cc_matViewProjInv);
  #endif
  #if CC_USE_FOG != 4
    float fogFactor = 1.0;
    CC_TRANSFER_FOG_BASE(worldPos, fogFactor);
    CC_APPLY_FOG_BASE(color, fogFactor);
  #endif
  if (!DebugViewNeedDisplayOriginalData()) {
    #if CC_USE_FLOAT_OUTPUT
      color.rgb = HDRToLDR(color.rgb);
      color.rgb = LinearToSRGB(color.rgb);
    #endif
  }
  return color;
}
void main () {
  vec4 o = texture2D(u_texSampler, v_uv);
  gl_FragColor = CCFragOutput_PostProcess(o);
}
